		class ButtonSelectorTracker {
			constructor() {
				this.init();
			}
			
			init() {
				// Делегирование событий
				document.addEventListener('click', this.handleClick.bind(this));
				
				// Показываем текущий сохраненный селектор
				this.showCurrentSelector();
			}
			
			handleClick(event) {
				const button = event.target.closest('[class*="fusion-button"][class*="button-"]');
				
				if (button) {
					// Генерируем разные варианты селекторов
					const selectors = this.generateSelectors(button);
					
					// Сохраняем лучший селектор
					this.saveBestSelector(selectors);
					
					// Показываем информацию
					console.log('Селекторы для кнопки:', selectors);
					console.log('Выбран селектор:', selectors.best);
					
					// Обновляем отображение
					this.showCurrentSelector();
				}
			}
			
			generateSelectors(button) {
				const classes = Array.from(button.classList);
				const buttonNumberClass = classes.find(cls => cls.match(/^button-\d+$/));
				
				// Вариант 1: Только fusion-button + button-X
				const simpleSelector = buttonNumberClass ? 
					`.fusion-button.${buttonNumberClass}` : 
					null;
				
				// Вариант 2: Все основные классы
				const mainClasses = classes.filter(cls => 
					cls.includes('fusion-button') || 
					cls.includes('button-') ||
					cls === 'button-flat'
				);
				const mainSelector = '.' + mainClasses.join('.');
				
				// Вариант 3: Только номер кнопки
				const numberOnlySelector = buttonNumberClass ? 
					`.${buttonNumberClass}` : 
					null;
				
				// Определяем лучший селектор
				let bestSelector = simpleSelector || mainSelector;
				
				// Проверяем уникальность селекторов
				if (simpleSelector && document.querySelectorAll(simpleSelector).length === 1) {
					bestSelector = simpleSelector;
				} else if (document.querySelectorAll(mainSelector).length === 1) {
					bestSelector = mainSelector;
				}
				
				return {
					simple: simpleSelector,
					main: mainSelector,
					numberOnly: numberOnlySelector,
					best: bestSelector,
					buttonNumber: buttonNumberClass ? buttonNumberClass.replace('button-', '') : null
				};
			}
			
			saveBestSelector(selectors) {
				// Сохраняем лучший селектор
				if (selectors.best) {
					this.setCookie('fusion_button_selector', selectors.best);
				}
				
				// Также сохраняем номер кнопки отдельно
				if (selectors.buttonNumber) {
					this.setCookie('fusion_button_number', selectors.buttonNumber);
				}
				
				// Сохраняем все варианты в localStorage
				try {
					localStorage.setItem('fusion_button_selectors', JSON.stringify(selectors));
				} catch (e) {
					console.warn('Не удалось сохранить в localStorage:', e);
				}
			}
			
			setCookie(name, value, days = 30) {
				const expires = new Date(Date.now() + days * 864e5).toUTCString();
				document.cookie = `${name}=${encodeURIComponent(value)};expires=${expires};path=/`;
			}
			
			getCookie(name) {
				const nameEQ = name + "=";
				const ca = document.cookie.split(';');
				
				for(let i = 0; i < ca.length; i++) {
					let c = ca[i].trim();
					if (c.indexOf(nameEQ) === 0) {
						return decodeURIComponent(c.substring(nameEQ.length));
					}
				}
				return null;
			}
			
			getCurrentSelector() {
				return this.getCookie('fusion_button_selector');
			}
			
			showCurrentSelector() {
				const selector = this.getCurrentSelector();
				if (selector) {
					console.log('Текущий сохраненный селектор:', selector);
					
					// Проверяем, находится ли кнопка на странице
					const button = document.querySelector(selector);
					if (button) {
						console.log('Кнопка найдена на странице');
					} else {
						console.log('Кнопка НЕ найдена на странице');
					}
				}
			}
			
			// Функция для клика по сохраненной кнопке
			clickSavedButton() {
				const selector = this.getCurrentSelector();
				
				if (!selector) {
					console.log('Селектор не найден в куки');
					return false;
				}
				
				const button = document.querySelector(selector);
				
				if (button) {
					button.click();
					console.log('Клик по кнопке с селектором:', selector);
					return true;
				} else {
					console.log('Кнопка не найдена по селектору:', selector);
					
					// Пробуем найти по номеру
					const buttonNumber = this.getCookie('fusion_button_number');
					if (buttonNumber) {
						const fallbackSelector = `.fusion-button.button-${buttonNumber}`;
						const fallbackButton = document.querySelector(fallbackSelector);
						
						if (fallbackButton) {
							fallbackButton.click();
							console.log('Клик по кнопке с селектором:', fallbackSelector);
							return true;
						}
					}
					
					return false;
				}
			}
		}

